        JSONObject tender = new JSONObject();

        query data = new query();
        HashMap location = new HashMap();
        HashMap person = new HashMap();
        HashMap palette = new HashMap();
        int pkLocation = 0;
        int pkPerson = 0;

        try (PrintWriter out = response.getWriter()) {
            try {
                /*Class.forName(jdbcDriver).newInstance();
                Connection conn = DriverManager.getConnection(jdbcURL, user, DBpassword);
                Statement stmt = conn.createStatement();*/
                if (fname == null || lname == null || fname == "" || lname == ""
                        || password == null || conPassword == null || (password == null ? conPassword != null : !password.equals(conPassword))
                        || emailAddress == null || address == null || city == null || province == null || country == null
                        || emailAddress == "" || address == "" || city == "" || province == "" || country == "") {
                    out.println(tender.put("message", "Please Fill in All Fields"));
                } else if (password.length() < 7) {
                    out.println(tender.put("message", "Passwords must be longer than 6 characters"));
                } else if (data.exists("person", person)) {
                    out.println(tender.put("message", "A user with that email already exists."));
                } else if (!ptr.matcher(emailAddress).matches()) {
                    out.println(tender.put("message", "enter a valid email address"));
                } else {
                    person.clear();
                    MessageDigest sha = MessageDigest.getInstance("SHA-1");
                    byte[] hashOne = sha.digest(password.getBytes());
                    String modPass = hexEncode(hashOne);
                    //out.println(modPass);
                    location.put("address", address);
                    location.put("city", toUpperCase(city));
                    location.put("country", toUpperCase(country));
                    location.put("province", toUpperCase(province));
                    pkLocation = data.insert("location", location);

                    person.put("firstname", toUpperCase(fname));
                    person.put("lastname", toUpperCase(lname));
                    person.put("password", modPass);
                    person.put("email", emailAddress);
                    person.put("location_pk", pkLocation);
                    pkPerson = data.insert("person", person);

                    HashMap id = new HashMap();
                    id.put("user_id", "5");

                    for (Object foods : data.getManyRows("palette", "foodtype", id)) {
                        palette.put("user_id", pkPerson);
                        palette.put("foodtype", foods);
                        palette.put("preference", true);
                        data.insert("palette", palette);
                        palette.clear();
                    }
                    out.println(tender.put("message", "OK"));
                }
            } catch (Exception e) {

                out.println("<p>" + e + "</p>");
            }
        }
